import React, { useEffect, useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import PaymentForm from './PaymentForm';

const stripePromise = loadStripe('pk_test_51PjJtcRw63Esxq07UkjI1nB0el5ynzm9z20BugidUX2SsQOCvBIDCkYCRZzmOORveByhs4AqeSV63eTbUJKSznBE00frzGquA9');

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [clientSecret, setClientSecret] = useState('');

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:8000/api/products/');
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleProductSelect = async (product) => {
        try {
            const response = await fetch(
                `http://localhost:8000/api/products/${product.id}/create_payment_intent/`,
                {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );
            const data = await response.json();

            if (data.error) {
                throw new Error(data.error);
            }

            setClientSecret(data.clientSecret);
            setSelectedProduct(product);
        } catch (error) {
            console.error('Error creating payment intent:', error);
            alert('Failed to initiate payment. Please try again.');
        }
    };

    return (
        <div className="product-list-container max-w-7xl mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6 text-center">Our Products</h1>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {products.map((product) => (
                    <div key={product.id} className="product-card border p-6 rounded-lg shadow-md hover:shadow-lg transition">
                        <h2 className="product-name text-xl font-semibold text-gray-800">{product.name}</h2>
                        <p className="product-description text-gray-600 mt-2">{product.description}</p>
                        <p className="product-price text-lg font-bold text-blue-600 mt-2">${product.price}</p>
                        <button
                            onClick={() => handleProductSelect(product)}
                            className="buy-button bg-blue-500 text-white py-2 px-4 mt-4 rounded-full hover:bg-blue-600 focus:outline-none"
                        >
                            Buy Now
                        </button>
                    </div>
                ))}
            </div>

            {selectedProduct && clientSecret && (
                <Elements stripe={stripePromise}>
                    <PaymentForm
                        clientSecret={clientSecret}
                        product={selectedProduct}
                    />
                </Elements>
            )}

            <style jsx>{`
                .product-list-container {
                    background-color: #f9fafb;
                    padding: 20px;
                    border-radius: 8px;
                }

                .product-card {
                    background-color: #fff;
                    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
                    border-radius: 8px;
                    transition: transform 0.3s ease, box-shadow 0.3s ease;
                }

                .product-card:hover {
                    transform: translateY(-5px);
                    box-shadow: 0 6px 15px rgba(0, 0, 0, 0.1);
                }

                .product-name {
                    color: #333;
                    font-size: 1.25rem;
                    margin-bottom: 10px;
                }

                .product-description {
                    font-size: 1rem;
                    color: #4a5568;
                    line-height: 1.5;
                }

                .product-price {
                    color: #1d4ed8;
                    font-size: 1.25rem;
                    margin-top: 8px;
                }

                .buy-button {
                    background-color: #3b82f6;
                    transition: background-color 0.3s ease;
                    cursor: pointer;
                }

                .buy-button:hover {
                    background-color: #2563eb;
                }

                .buy-button:focus {
                    outline: none;
                }
            `}</style>
        </div>
    );
};

export default ProductList;
