import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const PaymentForm = ({ clientSecret, product }) => {
    const stripe = useStripe();
    const elements = useElements();
    const [error, setError] = useState(null);
    const [processing, setProcessing] = useState(false);
    const [succeeded, setSucceeded] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setProcessing(true);

        try {
            if (!stripe || !elements) {
                throw new Error('Stripe has not loaded');
            }

            const { error, paymentIntent } = await stripe.confirmCardPayment(
                clientSecret,
                {
                    payment_method: {
                        card: elements.getElement(CardElement),
                    },
                }
            );

            if (error) {
                throw error;
            }

            // Confirm payment on backend
            const response = await fetch(
                `http://localhost:8000/api/products/${product.id}/confirm_payment/`,
                {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        payment_intent_id: paymentIntent.id,
                    }),
                }
            );

            const data = await response.json();

            if (data.error) {
                throw new Error(data.error);
            }

            setSucceeded(true);
            setError(null);
        } catch (err) {
            setError(err.message);
        } finally {
            setProcessing(false);
        }
    };

    return (
        <div className="max-w-md mx-auto mt-8 p-4 border rounded">
            <h2 className="text-xl mb-4">Complete Purchase: {product.name}</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <CardElement
                        options={{
                            style: {
                                base: {
                                    fontSize: '16px',
                                    color: '#424770',
                                    '::placeholder': {
                                        color: '#aab7c4',
                                    },
                                },
                                invalid: {
                                    color: '#9e2146',
                                },
                            },
                        }}
                    />
                </div>

                {error && (
                    <div className="text-red-500 mb-4">
                        {error}
                    </div>
                )}

                {succeeded && (
                    <div className="text-green-500 mb-4">
                        Payment successful!
                    </div>
                )}

                <button
                    type="submit"
                    disabled={processing || !stripe || succeeded}
                    className="w-full bg-blue-500 text-white py-2 px-4 rounded disabled:opacity-50"
                >
                    {processing ? 'Processing...' : `Pay $${product.price}`}
                </button>
            </form>
        </div>
    );
};

export default PaymentForm;
